//mobile-menu
let mobileMenu = document.querySelector('.mobile_menu');
let burgerMenu = document.querySelector('.gamburger-menu');

burgerMenu.addEventListener('click', fburgerActive);
    
function fburgerActive(){
    mobileMenu.classList.toggle('mobile_menu_active')
    burgerMenu.classList.toggle('burger')
    }

